var rp = require("../");

var name = rp.variable("World");

var greeding = name.computed(function(name) {
	return "Hello " + name + "!";
});

rp.ever(function() {
	console.log(greeding());
});
// logs: Hello World!

setTimeout(function() {
	name.set("You");
	// logs: Hello You!
}, 500);

setTimeout(function() {
	rp.atomic(function() {
		name.set("John");
		name.set("Joe");
	});
	// logs: Hello Joe!
}, 1000);